    require_once("db.php");

    if(isset($_POST["user"]) AND isset($_POST["password"]) AND isset($_POST["email"])){
        $login = $_POST["user"];
        $email = $_POST["email"];
        $hash = password_hash($_POST["password"], PASSWORD_DEFAULT);

        $req = $pdo->prepare('INSERT INTO users(username, password, email, created_on, last_login) VALUES (:login, :password, :email, current_timestamp, current_timestamp)');
        $req->execute(array(
            "login" => $login, 
            "password" => $hash,
            "email" => $email,
        ));
    }
